var Generator = require('generate-js');

var Tree = Generator.generate(function Tree(bars, struct) {
    var _ = this;

    _.bars = bars;
    _.struct = struct;


});

Tree.definePrototype({
    update: function update(context) {
        var _ = this;

        var insertVal = _.insert(context);

        if (!equals(_.insertVal, insertVal)) {
            _.insertVal = insertVal;

            _._update(insertVal);
        }
    }
});

module.exports = Tree;
